build.script:
# This is a hotfix for https://github.com/NVIDIA/cccl/issues/4967, backporting
# https://github.com/NVIDIA/cccl/pull/4972 on the fly for the installed headers
{%- if match(cuda_compiler_version, "12.*") and linux and x86_64 %}
CCCL_PREPROCESSOR_H_TO_PATCH="$PREFIX/targets/x86_64-linux/include/cuda/std/__cccl/preprocessor.h"
{%- endif %}

{%- if match(cuda_compiler_version, "12.*") and linux and aarch64 %}
CCCL_PREPROCESSOR_H_TO_PATCH="$PREFIX/targets/sbsa-linux/include/cuda/std/__cccl/preprocessor.h"
{%- endif %}

{%- if match(cuda_compiler_version, "12.*") and linux %}
ROOT_DIR="$(dirname "${CCCL_PREPROCESSOR_H_TO_PATCH}")/../../../.."
patch -p1 -d "${ROOT_DIR}" -i "${RECIPE_DIR}/cuda12_cccl_arch_all.patch"
{%- endif %}

echo hello
LLAMA_ARGS="-DLLAMA_BUILD_TESTS=OFF"

{%- macro llama_args(value) %}
LLAMA_ARGS="${LLAMA_ARGS} -DLLAMA_${{ value }}"
{%- endmacro %}

{%- macro cmake_args(value) -%}
LLAMA_ARGS="${LLAMA_ARGS} -D${{ value }}"
{%- endmacro %}

{% macro ggml_args(value) -%}
LLAMA_ARGS="${LLAMA_ARGS} -DGGML_${{ value }}"
{%- endmacro %}

${{ cmake_args("BUILD_SHARED_LIBS=ON") }}
${{ llama_args("CURL=ON") }}

{%- if osx and arm64 %}
${{ ggml_args("NATIVE=OFF") }}
${{ ggml_args("AVX=OFF") }}
${{ ggml_args("AVX2=OFF") }}
${{ ggml_args("FMA=OFF") }}
${{ ggml_args("F16C=OFF") }}
${{ ggml_args("METAL=ON") }}
${{ ggml_args("ACCELERATE=ON") }}
{%- endif %}

{%- if osx and x86_64 %}
${{ ggml_args("METAL=OFF") }}
${{ ggml_args("ACCELERATE=ON") }}
{%- endif %}

{%- if cuda_compiler_version | default("None") != "None" %}
${{ ggml_args("CUDA=ON") }}
# Exclude sm_120 (compute capability 12.0) as it doesn't support MXFP4 instructions
# Explicitly list all architectures except 120
${{ cmake_args("CMAKE_CUDA_ARCHITECTURES=80-virtual;86-real;89-real;120a-real;121a-real") }}
{%- endif %}

{%- if linux and x86_64 and cuda_compiler_version == "None" %}
${{ ggml_args("BLAS=ON") }}

{%- if blas_impl == "mkl" %}
${{ ggml_args("BLAS_VENDOR=Intel10_64_dyn") }}
{%- endif %}

{%- endif %}

echo $LLAMA_ARGS
cmake -S . -B build -G Ninja ${CMAKE_ARGS} ${LLAMA_ARGS}
cmake --build build
cmake --install build

